import React from "react";

function Loader() {
    const loaderStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
    };

    const spinnerStyle = {
        width: '50px',
        height: '50px',
        border: '6px solid rgba(0, 0, 0, 0.1)',
        borderTop: '6px solid #3498db', // Color of the spinning border
        borderRadius: '50%',
        animation: 'spin 1s linear infinite',
    };

    return (
        <div style={loaderStyle}>
            <div style={spinnerStyle}></div>
        </div>
    );
}

export default Loader;
