function Loader() {
    const loaderStyle = {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
    };

    const squareStyle = {
      width: "50px",
      height: "50px",
      backgroundColor: "#3498db",
      animation: "bounce 1.5s infinite ease-in-out",
    };

    const loaderContainerStyle = {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    };

    return (
      <div style={loaderStyle}>
        <div style={loaderContainerStyle}>
          <div style={squareStyle}></div>
        </div>
        <style>
          {`
            @keyframes bounce {
              0%, 100% {
                transform: scale(1);
              }
              50% {
                transform: scale(1.5);
              }
            }
          `}
        </style>
      </div>
    );
  }

  export default Loader;
