const Loader = () => {
  const loaderStyles = {
    "--w": "10ch",
    fontWeight: "bold",
    fontFamily: "monospace",
    fontSize: "30px",
    lineHeight: "1.2em",
    letterSpacing: "10ch",
    width: "10ch",
    overflow: "hidden",
    whiteSpace: "nowrap",
    color: "#0000",
    animation: "l17 2s infinite"
  };

  const keyframes = `
    @keyframes l17 {
      0% {text-shadow: calc(0*var(--w)) -1.2em #000, calc(-1*var(--w)) -1.2em #000, calc(-2*var(--w)) -1.2em #000, calc(-3*var(--w)) -1.2em #000, calc(-4*var(--w)) -1.2em #000, 
        calc(-5*var(--w)) -1.2em #000, calc(-6*var(--w)) -1.2em #000, calc(-7*var(--w)) -1.2em #000, calc(-8*var(--w)) -1.2em #000, calc(-9*var(--w)) -1.2em #000}
      4% {text-shadow: calc(0*var(--w)) 0 #000, calc(-1*var(--w)) -1.2em #000, calc(-2*var(--w)) -1.2em #000, calc(-3*var(--w)) -1.2em #000, calc(-4*var(--w)) -1.2em #000, 
        calc(-5*var(--w)) -1.2em #000, calc(-6*var(--w)) -1.2em #000, calc(-7*var(--w)) -1.2em #000, calc(-8*var(--w)) -1.2em #000, calc(-9*var(--w)) -1.2em #000}
      8% {text-shadow: calc(0*var(--w)) 0 #000, calc(-1*var(--w)) 0 #000, calc(-2*var(--w)) -1.2em #000, calc(-3*var(--w)) -1.2em #000, calc(-4*var(--w)) -1.2em #000, 
        calc(-5*var(--w)) -1.2em #000, calc(-6*var(--w)) -1.2em #000, calc(-7*var(--w)) -1.2em #000, calc(-8*var(--w)) -1.2em #000, calc(-9*var(--w)) -1.2em #000}
      100% {text-shadow: calc(0*var(--w)) 1.2em #000, calc(-1*var(--w)) 1.2em #000, calc(-2*var(--w)) 1.2em #000, calc(-3*var(--w)) 1.2em #000, calc(-4*var(--w)) 1.2em #000, 
        calc(-5*var(--w)) 1.2em #000, calc(-6*var(--w)) 1.2em #000, calc(-7*var(--w)) 1.2em #000, calc(-8*var(--w)) 1.2em #000, calc(-9*var(--w)) 1.2em #000}
    }
  `;

  return (
    <>
      <style>
        {keyframes}
      </style>
      <div style={loaderStyles}>Loading...</div>
    </>
  );
};

export default Loader;
