function Loader() {
    const loaderStyle = {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
    };

    const circleStyle = {
      width: "100px",
      height: "100px",
      borderRadius: "50%",
      border: "10px solid #f3f3f3",
      borderTop: "10px solid #3498db",
      animation: "spin 1s linear infinite",
    };

    return (
      <div style={loaderStyle}>
        <div style={circleStyle}></div>
      </div>
    );
  }

  export default Loader;
