import ProductCard from "./ProductCard";

function ProductsTable({ data }) {
    return (
        <table border="1px" style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
                <tr style={{ backgroundColor: "#91DDCF", fontSize: "20px", height: "50px" }}>
                    <th style={{ padding: "10px" }}>Sr.no</th>
                    <th style={{ padding: "10px" }}>Brand</th>
                    <th style={{ padding: "10px" }}>Category</th>
                    <th style={{ padding: "10px" }}>Price</th>
                </tr>
            </thead>
            <tbody>
                {data.map((item, index) => (
                    <ProductCard 
                        key={item.id} 
                        id={item.id} 
                        brand={item.brand} 
                        category={item.category} 
                        price={item.price} 
                        index={index + 1} // Pass the index as Sr.no
                    />
                ))}
            </tbody>
        </table>
    );
}

export default ProductsTable;
