function Loader() {
    const loaderStyle = {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
    };

    const circleStyle = {
      border: "8px solid rgba(0, 0, 0, 0.1)",
      borderTop: "8px solid #3498db", // Color for the top section of the spinner
      borderRadius: "50%",
      width: "60px",
      height: "60px",
      animation: "spin 1.5s linear infinite",
    };

    const spinAnimation = `
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `;

    return (
      <div style={loaderStyle}>
        <style>{spinAnimation}</style>
        <div style={circleStyle}></div>
      </div>
    );
  }

  export default Loader;
