import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../Context/AuthContext";
import ProductsTable from "../Components/ProductsTable";
import axios from "axios";
import Loader from "../Components/Loader"; // Import the Loader component

function Dashboard() {
  const { logoutUser, authState } = useContext(AuthContext);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false); // Loading state

  useEffect(() => {
    setLoading(true); // Set loading to true before making the API call
    axios
      .get("https://dbioz2ek0e.execute-api.ap-south-1.amazonaws.com/mockapi/get-tech-products")
      .then((res) => {
        setData(res.data.data);
        setLoading(true); // Set loading to false after receiving the data
      })
      .catch((err) => {
        console.log(err);
        setLoading(false); // Set loading to false even in case of error
      });
  }, []);

  if (loading) {
    return <Loader />; // Render Loader component while data is being fetched
  }

  return (
    <div>
      <h1 style={{ color: "red" }}>Dashboard</h1>
      <div>
        <button
          data-testid="logout-btn"
          onClick={logoutUser}
          style={{ height: "50px", width: "100px", fontSize: "18px" }}
        >
          Logout
        </button>
        <p style={{ backgroundColor: "gray", width: "20%", marginLeft: "40%" }}>
          Token:
          <b
            data-testid="user-token"
            style={{ color: "white", fontWeight: "bold" }}
          >
            {authState.token}
          </b>
        </p>
      </div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <ProductsTable data={data} />
      </div>
    </div>
  );
}

export default Dashboard;
